    private void convertCFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_convertCFActionPerformed
        String c = celsiusText.getText();
        Double celsius = Double.parseDouble(c);
        double fahrenheit = celsius*1.8+32;
        farText.setText(Double.toString(fahrenheit));
        farText.setCaretPosition(0);
    }//GEN-LAST:event_convertCFActionPerformed

