    private void convertFCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_convertFCActionPerformed
        String f = farText.getText();
        Double fahrenheit = Double.parseDouble(f);
        double celsius = (fahrenheit-32)/1.8;
        celsiusText.setText(Double.toString(celsius));
        celsiusText.setCaretPosition(0);
    }//GEN-LAST:event_convertFCActionPerformed

    private void formMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_formMouseClicked
        farText.setText("");
        celsiusText.setText("");
    }//GEN-LAST:event_formMouseClicked

